          $first=1;
          $StrEspecialist = $esp;
        }
        else {
          $StrEspecialist .= "|".$esp;
        }
      }
        
      $StrLanguage ="";
      $first=0;
      foreach($theidlanguage as $lan) {
        if($first==0) {
          $first=1;
          $StrLanguage = $lan;
        }
        else {
           $StrLanguage .= "|".$lan;
